import React, { useState, useEffect } from 'react';
import { auth, storage, db } from './firebaseConfig';
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  updateProfile,
  signOut
} from 'firebase/auth';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { doc, setDoc } from 'firebase/firestore';
import UserWorkspace from './components/UserWorkspace';
import LoginForm from './components/LoginForm';
import SignUpForm from './components/SignUpForm';
import { Bell, Globe } from 'lucide-react';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isSignUp, setIsSignUp] = useState(false);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleSignIn = async (email, password) => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error("Error signing in:", error.message);
      alert(error.message);
    }
  };

  const handleSignUp = async (email, password, firstName, lastName, photoFile, salutation) => {
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      let photoURL = '';

      if (photoFile) {
        const storageRef = ref(storage, `profile_photos/${userCredential.user.uid}`);
        await uploadBytes(storageRef, photoFile);
        photoURL = await getDownloadURL(storageRef);
      }

      await updateProfile(userCredential.user, {
        displayName: `${firstName} ${lastName}`,
        photoURL: photoURL
      });

      await setDoc(doc(db, 'users', userCredential.user.uid), {
        email: email,
        firstName: firstName,
        lastName: lastName,
        salutation: salutation,
        photoURL: photoURL,
        createdAt: new Date()
      });

    } catch (error) {
      console.error("Error signing up:", error.message);
      alert(error.message);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error("Error signing out:", error.message);
      alert(error.message);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-indigo-500"></div>
      </div>
    );
  }

  return (
    <div className="App">
      {!user ? (
        <div className="auth-container">
          <header className="app-header bg-indigo-900 text-white p-4 flex justify-between items-center">
            <h1 className="text-2xl font-bold flex items-center">
              <span className="mr-2">🚢</span> Oceania PMIS
            </h1>
            <div>
              <button
                onClick={() => setIsSignUp(false)}
                className="bg-blue-700 text-white px-4 py-2 rounded mr-2 hover:bg-blue-600 transition duration-300"
              >
                Login
              </button>
              <button
                onClick={() => setIsSignUp(true)}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-500 transition duration-300"
              >
                Sign up
              </button>
            </div>
          </header>
          <main className="p-6 bg-gray-100 min-h-screen">
            <div className="max-w-md mx-auto bg-white p-8 rounded-lg shadow-md">
              {isSignUp ? (
                <SignUpForm onSignUp={handleSignUp} />
              ) : (
                <LoginForm onSignIn={handleSignIn} />
              )}
            </div>
          </main>
        </div>
      ) : (
        <div className="flex h-screen bg-gray-100">
          {/* Sidebar */}
          <aside className="w-64 bg-white shadow-md">
            <div className="p-4 border-b">
              <h1 className="text-2xl font-bold flex items-center">
                <span className="mr-2">🚢</span> Oceania PMIS
              </h1>
            </div>
            <nav className="mt-4">
              {/* Add your navigation items here */}
            </nav>
          </aside>

          {/* Main content */}
          <main className="flex-1 overflow-y-auto">
            {/* Header */}
            <header className="bg-white shadow-sm p-4 flex justify-between items-center">
              <h2 className="text-xl font-semibold">Dashboard</h2>
              <div className="flex items-center space-x-4">
                <Globe size={20} />
                <span>EN</span>
                <Bell size={20} />
                <img src={user.photoURL || "/api/placeholder/40/40"} alt="User" className="w-8 h-8 rounded-full" />
                <div className="flex flex-col">
                  <button onClick={handleSignOut} className="bg-indigo-700 text-white px-3 py-1 rounded text-sm hover:bg-indigo-600 transition duration-300">Logout</button>
                  <button className="text-indigo-700 text-sm hover:text-indigo-600 transition duration-300">Profile</button>
                </div>
              </div>
            </header>

            {/* UserWorkspace component */}
            <UserWorkspace user={user} />
          </main>
        </div>
      )}
    </div>
  );
}

export default App;
