// App.js
import React, { useState, useEffect } from 'react';
import { auth, storage, db } from './firebaseConfig';
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  updateProfile,
  signOut
} from 'firebase/auth';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { doc, setDoc } from 'firebase/firestore';
import UserWorkspace from './components/UserWorkspace';
import UserAccountManagement from './components/UserAccountManagement';
import LoginForm from './components/LoginForm';
import SignUpForm from './components/SignUpForm';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isSignUp, setIsSignUp] = useState(false);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const handleSignIn = async (email, password) => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (error) {
      console.error("Error signing in:", error.message);
      alert(error.message);
    }
  };

  const handleSignUp = async (email, password, firstName, lastName, photoFile) => {
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      let photoURL = '';

      if (photoFile) {
        const storageRef = ref(storage, `profile_photos/${userCredential.user.uid}`);
        await uploadBytes(storageRef, photoFile);
        photoURL = await getDownloadURL(storageRef);
      }

      await updateProfile(userCredential.user, {
        displayName: `${firstName} ${lastName}`,
        photoURL: photoURL
      });

      // Store additional user info in Firestore
      await setDoc(doc(db, 'users', userCredential.user.uid), {
        email: email,
        firstName: firstName,
        lastName: lastName,
        photoURL: photoURL,
        createdAt: new Date()
      });

    } catch (error) {
      console.error("Error signing up:", error.message);
      alert(error.message);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error("Error signing out:", error.message);
      alert(error.message);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      {!user ? (
        <div className="auth-container">
          <header className="app-header">
            <h1>ðŸš¢ Oceania PMIS</h1>
            <div>
              <button onClick={() => setIsSignUp(false)}>Login</button>
              <button onClick={() => setIsSignUp(true)}>Sign up</button>
            </div>
          </header>
          {isSignUp ? (
            <SignUpForm onSignUp={handleSignUp} />
          ) : (
            <LoginForm onSignIn={handleSignIn} />
          )}
        </div>
      ) : (
        <>
          <header className="app-header">
            <h1>ðŸš¢ Oceania PMIS</h1>
            <div>
              <span>Welcome, {user.displayName}</span>
              <button onClick={handleSignOut}>Logout</button>
            </div>
          </header>
          <main>
            <UserWorkspace user={user} />
            <UserAccountManagement />
          </main>
        </>
      )}
    </div>
  );
}

export default App;
