import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { auth } from './firebaseConfig';
import api from './services/api';
import UserWorkspace from './components/UserWorkspace';
import UserAccountManagement from './components/UserAccountManagement';
import LoginForm from './components/LoginForm';
import SignUpForm from './components/SignUpForm';
import ForgotPassword from './components/ForgotPassword';
import ResetPassword from './components/ResetPassword';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      setUser(user);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <div className="App">
        <header className="app-header">
          <h1>ðŸš¢ Oceania PMIS</h1>
          {user && (
            <div>
              <span>Welcome, {user.displayName}</span>
              <button onClick={api.logoutUser}>Logout</button>
            </div>
          )}
        </header>
        <main>
          <Routes>
            {!user ? (
              <>
                <Route path="/" element={<LoginForm onSignIn={api.loginUser} />} />
                <Route path="/signup" element={<SignUpForm onSignUp={api.registerUser} />} />
                <Route path="/forgot-password" element={<ForgotPassword />} />
                <Route path="/reset-password" element={<ResetPassword />} />
              </>
            ) : (
              <>
                <Route path="/" element={<UserWorkspace user={user} />} />
                <Route path="/account-management" element={<UserAccountManagement />} />
              </>
            )}
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
