import axios from 'axios';
import { auth } from '../firebaseConfig';

const API_URL = 'http://localhost:5000/api';

const authAxios = axios.create({
  baseURL: API_URL,
});

authAxios.interceptors.request.use(async (config) => {
  const token = await auth.currentUser?.getIdToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const getResources = () => authAxios.get('/resources');
export const getUsers = () => authAxios.get('/users');
export const createUser = (userData) => authAxios.post('/users', userData);
export const updateUser = (userId, userData) => authAxios.put(`/users/${userId}`, userData);
export const deleteUser = (userId) => authAxios.delete(`/users/${userId}`);
