import React, { useState } from 'react';
import { sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebaseConfig';
import { Link } from 'react-router-dom';
import './AuthForms.css';

function ForgotPassword() {
    const [email, setEmail] = useState('');
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            await sendPasswordResetEmail(auth, email);
            setIsSubmitted(true);
        } catch (error) {
            setError('Failed to send reset email. Please check your email address and try again.');
            console.error('Error sending reset email:', error);
        }
    };

    if (isSubmitted) {
        return (
            <div className="auth-form">
                <h2>ðŸš¢ Oceania PMIS</h2>
                <p>Forgot Password?</p>
                <p>Reset password details in your inbox!</p>
                <button className="auth-button" onClick={() => setIsSubmitted(false)}>Send again</button>
                <p>
                    <Link to="/login">Back to Login</Link>
                </p>
            </div>
        );
    }

    return (
        <div className="auth-form">
            <h2>ðŸš¢ Oceania PMIS</h2>
            <p>Forgot Password?</p>
            <p>Enter your email address and we will send you instructions to reset your password.</p>
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email">Email Address*</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Email Address*"
                        required
                    />
                </div>
                <button type="submit" className="auth-button">Send Reset Link</button>
            </form>
            <p>
                <Link to="/login">Back to Login</Link>
            </p>
        </div>
    );
}

export default ForgotPassword;
